import React, { useEffect, useState, useRef } from 'react';
//import { useWebSocket } from '../WebSocketContext';
import './playboard.css';

const Playboard = () => {
    const connection = useRef<WebSocket | null>(null);
    const [players, setPlayers] = useState<string[]>([]);
    const [coins, setCoins] = useState<number[]>([]);
    const [dealer, setDealer] = useState<string | null>(null);
    const [pot, setPot] = useState<number | null>(null);
    const [myCards, setMyCards] = useState<any | null>(null); // Update with correct type
    const [mySecondCard, setMySecondCard] = useState<any | null>(null); 


    const playerName = window.localStorage.getItem('playerName');
    const tableId = window.localStorage.getItem('tableId');

   
    const connectWebSocket = () => {
        if (!connection.current) {
            connection.current = new WebSocket('wss://your-websocket-url'); // Add your WebSocket URL here
            
            connection.current.onopen = () => {
                console.log('WebSocket connected');
                connection.current?.send(JSON.stringify({
                    action: 'playGame', 
                    playerName,
                    tableId
                }));
            };

            connection.current.onmessage = (e) => {
                const data = JSON.parse(e.data);
                console.log("Received data:", data);

                // Handle various WebSocket message types
                if (data.action === 'set-small-blind') {
                    console.log("You are the small blind");
                }

                if (data.players) {
                    const updatedPlayers = data.players.map((p: any) => ({
                        playerName: p.playerName,
                        playerCoin: p.playerCoin,
                        dealer: p.playerName === data.dealer,
                    }));

                    setPlayers(updatedPlayers.map(() => p.playerName));
                    setCoins(updatedPlayers.map(p => p.playerCoin));
                    setDealer(data.dealer);
                }

                if (data.card) {
                    setMyCards(data.card[0]);
                    setMySecondCard(data.card[1]);
                }

                if (data.pot !== undefined) setPot(data.pot);
            };
        }
    };

    useEffect(() => {
        connectWebSocket();

        // Cleanup on component unmount
        return () => {
            connection.current?.close();
            connection.current = null;
        };
    }, []);

    useEffect(() => {
        if (connection.current?.readyState === WebSocket.OPEN) {
            connection.current.send(JSON.stringify({
                action: 'set-small-blind', 
                playerName,
                tableId
            }));
        }
    }, [players, dealer, pot]);

    return (
        <div className="playboard">
            <div className="board-border">
                <div className="board">
                    {players.map((player, index) => (
                        <div key={index} className={`player player-${index + 1}`}>
                            <h2 className="player-name">{player}</h2>
                            {dealer === player && <h3>(Dealer)</h3>}
                        </div>
                    ))}
                </div>
            </div>
            <div className="pot">
                <h1>Pot: {pot}</h1>
            </div>
        </div>
    );
};

export default Playboard;
