import { useEffect, useState } from 'react';
import cardsType from '../types';
import './playboard.css';

const Playboard = () => {
    const [players, setPlayers] = useState<string[]>([]); // Array to hold player names
    const [myCards, setMyCards] = useState<cardsType | null>(null); // Initialize with null
    const [mySecondCard, setMySecodnCard] = useState<cardsType | null>(null);
      const [coins, setCoins] = useState<string[]>([]);
      const [images, setImages] = useState<string[]>([]);
    useEffect(() => {
        const connection = new WebSocket('ws://localhost:8080');

        connection.onopen = (e) => {
            console.log('WebSocket connection established.');
            const playerName = window.localStorage.getItem('playerName');
            const tableId = window.localStorage.getItem('tableId');
            connection.send(JSON.stringify({ action: 'playGame', playerName: playerName, tableId: tableId }));
        };

        try {
            connection.onmessage = (e) => {
                try {
                    const data = JSON.parse(e.data);
                    console.log('Parsed data:', data);

                    // Check if 'users' data exists
                    if (data.players) {
                        // Extract player names from the data
                        const playerNames = data.players.map((p: any) => p.playerName);
                        
                        const playerCoins = data.players.map((p: any) => p.playerCoin);

                        const playerImages = data.player.map((p:any) => p.playerImage)


                        // Get the playerName from localStorage
                        const storedPlayerName = window.localStorage.getItem('playerName');

                        // Find the index of the stored player name in the players array
                        const startIndex = playerNames.findIndex((name: string) => name === storedPlayerName);

                        // If the player's name exists in the array, reorder the array
                        if (startIndex !== -1) {
                            const reorderedPlayers = [
                                ...playerNames.slice(startIndex),
                                ...playerNames.slice(0, startIndex)
                            ];
                            setCoins(playerCoins)
                            setImages
                            setPlayers(reorderedPlayers);
                        } else {
                            // If the player's name is not found, just set the original array
                            setPlayers(playerNames);
                        }
                    }

               

                    // Check if 'card' data exists
                    if (data.card) {
                        setMyCards(data.card[0]);
                        setMySecodnCard(data.card[1])
                    }
                } catch (error) {
                    console.error('Error parsing data:', error);
                    console.log('Raw data received:', e.data);
                }
            };
        } catch (error) {
            console.log(error);
        }

        connection.onerror = (error) => {
            console.error('WebSocket Error:', error);
        };

        connection.onclose = () => {
            console.log('WebSocket connection closed.');
            window.localStorage.removeItem('tableId');
        };

        // Cleanup when the component unmounts
        return () => {
            connection.close();
            console.log('WebSocket connection closed on component unmount.');
        };
    }, []);

    return (
        <div className="playboard">
            <div className='board-border'>
                <div className='board'>
                    {/* Render reordered players in the appropriate cells */}
                    <div className='player player-1'>
                    {myCards&& myCards.card_link&& ( <div className='card-imagine-div'>
                        <div className='card-imagine'></div>
                        <div className='card-imagine'></div>
                        </div>)}
                        <h2 className='player-name'>{players[2] || ''}</h2>
                        <h2>{coins[2]}</h2>
                        </div>
                    <div className='player player-2'>
                        <div className='cards-div'>
                        {myCards && myCards.card_link && (
                        <img className='card first-card' src={require(`../cards/${myCards.card_link}`)} alt="" />
                    )}
                     {mySecondCard && mySecondCard.card_link && (
                        <img className='card first-card' src={require(`../cards/${mySecondCard.card_link}`)} alt="" />
                    )}
                           
                        </div>
                        
                        <h2 className='player-name'>{players[0] || ''}</h2>
                            <h2>{coins[0]}</h2>
                    </div>
                    <div className='player player-3'>
                    {myCards&& myCards.card_link&& ( <div className='card-imagine-div'>
                        <div className='card-imagine'></div>
                        <div className='card-imagine'></div>
                        </div>)}
                       
                        <h2 className='player-name'>{players[1] || ''}</h2>
                        <h2>{coins[1]}</h2>
                        </div>
                    <div className='player player-4'>
                    {myCards&& myCards.card_link&& ( <div className='card-imagine-div'>
                        <div className='card-imagine'></div>
                        <div className='card-imagine'></div>
                        </div>)}
                        <h2 className='player-name'>{players[3] || ''}</h2>
                        <h2>{coins[3]}</h2>
                        </div>
                </div>
            </div>
                   
            <div className='panel'>
                <button className='panel-btn fold'>Fold</button>
                <button className='panel-btn call'>Call</button>
                <button className='panel-btn raise'>Raise</button>
            </div>
        </div>
    );
};

export default Playboard;
