import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import './App.css';
import { WebSocketProvider } from './WebSocketContext';
import PokerComponent from './pages/PokerComponent';
import Playboard from './pages/Playboard';
import Login from './pages/Login';
import Dashboard from './pages/Dashboard';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isLoading, setIsLoading] = useState(true); // Loading state

  useEffect(() => {
    const token = window.localStorage.getItem('token');
    if (token) {
      setIsAuthenticated(true);
    }
    setIsLoading(false); // Authentication check is done
  }, []); // Runs once on mount

  if (isLoading) {
    return <div>Loading...</div>; // You can display a loading spinner or similar
  }

  return (
    <div className="App">
      <WebSocketProvider>
        <PokerComponent />
        <Playboard />
      </WebSocketProvider>

      <Routes>
        <Route path="/play/:id" element={isAuthenticated ? <Playboard /> : <Navigate to="/login" />} />
        <Route path="/" element={<Dashboard />} />
        <Route path="/login" element={isAuthenticated ? <Navigate to="/play" /> : <Login />} />
      </Routes>
    </div>
  );
}

export default App;
