import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

const PokerComponent = () => {
    // Move useState to the top level of the component
    const [test, setTest] = useState<string | null>(null);
    const navigate = useNavigate()
    useEffect(() => {
      
        const connection = new WebSocket('ws://localhost:8080');

        connection.onopen = () => {
            console.log('WebSocket connection established.');
            const playerName = window.localStorage.getItem('playerName');
           connection.send(JSON.stringify({ action: 'joinGame', playerName }));
        };

        connection.onmessage = (e) => {
            const data = JSON.parse(e.data);
            console.log(data);      
            setTest(data.message); // Assuming 'data' contains a 'message' field
                window.localStorage.setItem('tableId', data.tableId)
            if (data.tableId) {
                navigate(`/play/${data.tableId}`)
                window.localStorage.setItem('tableId', data.tableId)
            }
        };
        connection.onclose = () => {
            console.log('კავშირი პოკერ კომპონეტში დაიხურე');
            
        }



        // Cleanup when the component unmounts
        return () =>  connection.close();
    }, []);

    return (
        <div>
            <h1>{test}</h1>
        </div>
    );
};

export default PokerComponent;
