import { useEffect, useState } from 'react';
import cardsType from '../types';
import './playboard.css';

const Playboard = () => {
    const [players, setPlayers] = useState<string[]>([]);
    const [myCards, setMyCards] = useState<cardsType | null>(null);
    const [mySecondCard, setMySecondCard] = useState<cardsType | null>(null);
    
    useEffect(() => {
        const connection = new WebSocket('ws://localhost:8080');

        connection.onopen = () => {
            const playerName = window.localStorage.getItem('playerName');
            const tableId = window.localStorage.getItem('tableId');
            connection.send(JSON.stringify({ action: 'playGame', playerName, tableId }));
        };

        connection.onmessage = (e) => {
            const data = JSON.parse(e.data);
            if (data.players) {
                const playerNames = data.players.map((p: any) => p.playerName);
                const storedPlayerName = window.localStorage.getItem('playerName');
                const currentPlayerIndex = playerNames.findIndex(name => name === storedPlayerName);



                // Reorder players so current player is always first
                const reorderedPlayers = [
                    ...playerNames.slice(currentPlayerIndex),
                    ...playerNames.slice(0, currentPlayerIndex)
                ];
                setPlayers(reorderedPlayers);
            }
            if (data.card) {
                setMyCards(data.card[0]);
                setMySecondCard(data.card[1]);
            }
        };

        connection.onclose = () => {
            window.localStorage.removeItem('tableId');
        };

        return () => {
            connection.close();
        };
    }, []);

    return (
        <div className="playboard">
            <div className='board-border'>
                <div className='board'>
                    {players.map((player, index) => (
                        <div
                            key={player}
                            className={`player player-${index}`}
                            style={{ position: 'relative' }}
                        >
                            <h1 className={`player-name position-${index}`}>
                                {player}
                            </h1>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default Playboard;
