import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useWebSocket } from '../WebSocketContext';

const PokerComponent = () => {
    const playerName = window.localStorage.getItem('playerName');
    const [test, setTest] = useState<string | null>(null);
    const navigate = useNavigate();
    const ws = useWebSocket();

    useEffect(() => {
        if (!ws) {
            console.log('WebSocket connection is not established yet.');
            return;
        }

        console.log('WebSocket connection available:', ws);

        ws.send(JSON.stringify({ action: 'joinGame', playerName }));

        ws.onmessage = (e: MessageEvent) => {
            const data = JSON.parse(e.data);
            console.log(e.data);

            setTest(data.message);
            if (data.tableId) {
                navigate(`/play/${data.tableId}`);
                window.localStorage.setItem('tableId', data.tableId);
            }
        };

        return () => {
            ws.onmessage = null; // Clean up event listener on unmount
        };
    }, [ws, navigate, playerName]);

    return (
        <div>
            <h1>{test}</h1>
        </div>
    );
};

export default PokerComponent;
