import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { connectWebSocket, sendMessage, addMessageListener, closeWebSocket } from '../ws';

const PokerComponent = () => {
    const playerName = window.localStorage.getItem('playerName');
    const [test, setTest] = useState<string | null>(null);
    const navigate = useNavigate();

    useEffect(() => {
        const wss = connectWebSocket('ws://localhost:8080');
        sendMessage({ playerName: playerName, action: 'joinGame' });

        addMessageListener((data: any) => {
            console.log('PokerComponent received:', data);
            setTest(data.message);

            if (data.tableId) {
                window.localStorage.setItem('tableId', data.tableId);
                navigate(`/play/${data.tableId}`); // Do not close WebSocket
            }
        });

        return () => {
            // No WebSocket cleanup here
        };
    }, []);

   
    
    
    return (
        <div >
            <h1>{test}</h1>
        </div>
    );
};


export default PokerComponent;
