import React, { useEffect, useState, useRef } from 'react';
//import { useWebSocket } from '../WebSocketContext';
import './playboard.css';
import { Action } from '@remix-run/router';

const Playboard = () => {
  
    const [players, setPlayers] = useState<string[]>([]);
    const [coins, setCoins] = useState<number[]>([]);
    const [dealer, setDealer] = useState<string | null>(null);
    const [pot, setPot] = useState<number | null>(null);
    const [myCards, setMyCards] = useState<any | null>(null); // Update with correct type
    const [mySecondCard, setMySecondCard] = useState<any | null>(null); 


    const connection = useRef<WebSocket | null>(null);

    const playerName = window.localStorage.getItem('playerName');
    const tableId = window.localStorage.getItem('tableId');

   
    useEffect(() => {
        const connectWebSocket = () => {
            if (!connection.current) {
                connection.current = new WebSocket("ws://localhost:8080/tamashi");
    
                connection.current.onopen = () => {
                    connection.current?.send(JSON.stringify({
                        action: 'playGame',
                        playerName,
                        tableId
                    }));
                };
    
                connection.current.onmessage = (e) => {
                    const data = JSON.parse(e.data);
                    console.log("Received data:", data);
    
                    if (data.pot) {
                        setPot(data.pot);
                    }
    
                    if (data.action === 'set-small-blind') {
                        console.log('Small blind action:', data);
                        // Handle small blind update
                    }
    
                    if (data.players) {
                        const updatedPlayers = data.players.map((p: any) => ({
                            playerName: p.playerName,
                            playerCoin: p.playerCoin,
                            dealer: p.playerName === data.dealer,
                        }));
    
                        const startIndex = updatedPlayers.findIndex((p: any) => p.playerName === playerName);
                        const reorderedPlayers = [
                            ...updatedPlayers.slice(startIndex),
                            ...updatedPlayers.slice(0, startIndex),
                        ];
    
                        setPlayers(reorderedPlayers.map(p => p.playerName));
                        setCoins(reorderedPlayers.map(p => p.playerCoin));
                        setDealer(data.dealer);


                      
                    }
    
                    if (data.cards) {
                        setMyCards(data.cards[0]);
                        setMySecondCard(data.cards[1]);
                    }
                    let test = false

                    if (data.action === 'your Turn') {
                        const currentPlayer = data.currentPlayer;
                        if (currentPlayer === playerName) {
                            test = true
                        }
                    }
                };
            }
        };
    
        connectWebSocket();
    
       
    }, []);
    useEffect(() => {
        let test = false
        if (connection.current?.readyState === WebSocket.OPEN) {
            connection.current.send(JSON.stringify({
                action: 'set-blinds', 
                playerName,
                tableId
            }));

            test = true
        }

        if (test) {
            if (connection.current?.readyState === WebSocket.OPEN) {
                connection.current.send(JSON.stringify({
                    action: 'pre-flop', 
                    playerName,
                    tableId
                }));
    
              
            }
        }
    }, [players])

    return (
        <div className="playboard">
            <div className='board-border'>
                <div className='board'>
                    <div className='player player-1'>
                        
                        {myCards && myCards.card_link && (
                            <div className='card-imagine-div'>
                                <div className='card-imagine'></div>
                                <div className='card-imagine'></div>
                            </div>
                        )}
                        <h2 className='player-name'>{players[2] || ''}</h2>
                        <h2>{coins[2]}</h2>
                    </div>
                    <div className='player player-2'>
                        <div className='cards-div'>
                            {myCards && myCards.card_link && (
                                <img className='card first-card' src={require(`../cards/${myCards.card_link}`)} alt="" />
                            )}
                            {mySecondCard && mySecondCard.card_link && (
                                <img className='card first-card' src={require(`../cards/${mySecondCard.card_link}`)} alt="" />
                            )}
                        </div>
                        <h2 className='player-name'>{players[0] || ''}</h2>
                        <h2>{coins[0]}</h2>
                    </div>
                    <div className='player player-3'>
                        {myCards && myCards.card_link && (
                            <div className='card-imagine-div'>
                                <div className='card-imagine'></div>
                                <div className='card-imagine'></div>
                            </div>
                        )}
                        <h2 className='player-name'>{players[1] || ''}</h2>
                        <h2>{coins[1]}</h2>
                    </div>
                    <div className='pot'>
                        <h1>Pot: {pot}$</h1>
                        <h3>{dealer} is the dealer</h3>
                    </div>
                    <div className='player player-4'>
                        {myCards && myCards.card_link && (
                            <div className='card-imagine-div'>
                                <div className='card-imagine'></div>
                                <div className='card-imagine'></div>
                            </div>
                        )}
                        <h2 className='player-name'>{players[3] || ''}</h2>
                        <h2>{coins[3]}</h2>
                    </div>
                </div>
            </div>
            <div className='panel'>
                <button onClick={() => {
                     test === tr
                    connection.current?.send(JSON.stringify({tableId: tableId, playerName: playerName, action: 'fold'}))
                }} className='panel-btn fold'>Fold</button>
                <button  onClick={() => {
                    connection.current?.send(JSON.stringify({tableId: tableId, playerName: playerName, action: 'call'}))
                }} className='panel-btn call'>Call</button>
                <button className='panel-btn raise'>Raise</button>
            </div>
        </div>
    );
};

export default Playboard;
