import React, { createContext, useEffect, useRef, ReactNode,  } from 'react';

// Create context for WebSocket
const WebSocketContext = createContext<WebSocket | null>(null);

// Define WebSocketProvider component
export const WebSocketProvider: React.FC<{ children: ReactNode }> = async ({ children }) => {
    const wsRef = useRef<WebSocket | null>(null);

    useEffect(() => {
        // Initialize WebSocket once
        wsRef.current = new WebSocket('ws://localhost:8080');
        wsRef.current.onopen = () => console.log('WebSocket connection established.');

        wsRef.current.onerror = (error) => {
            console.error('WebSocket error: ', error);
        };

        wsRef.current.onclose = () => console.log('WebSocket closed.');

        return () => {
            wsRef.current?.close();
        };
    }, []);

    return (
        <WebSocketContext.Provider value={wsRef.current}>
            {children}
        </WebSocketContext.Provider>
    );
};

// Custom hook to access WebSocket context
export const useWebSocket = () => React.useContext(WebSocketContext);
