import { useEffect, useState, useRef } from 'react';
import cardsType from '../types';
import './playboard.css';
import { playersType } from '../types';
import axios from 'axios';
import { table } from 'console';
import { useParams } from 'react-router-dom';
const Playboard = () => {
    const [players, setPlayers] = useState<string[]>([]);
    const [myCards, setMyCards] = useState<cardsType | null>(null);
    const [mySecondCard, setMySecodnCard] = useState<cardsType | null>(null);
    const [coins, setCoins] = useState<string[]>([]);
    const [images, setImages] = useState<string[]>([]);
    const [pot, setPot] = useState<number>();
    const [dealer, setDealer] = useState();
    const [playerObj, setPlayerObj] = useState<playersType[]>([]);

    const {id} = useParams()

    // UseRef to persist WebSocket connection without reinitializing on re-renders
    const connection = useRef<WebSocket | null>(null);
    const connectWebSocket = () => {
    const ws = new WebSocket('ws://localhost:8080');
    
    ws.onopen = () => {
        console.log('WebSocket connection established.');
    };

    ws.onmessage = (e) => {
        const data = JSON.parse(e.data);
        console.log("Received message:", data);
        // Handle messages here
    };

    ws.onclose = () => {
        console.log("WebSocket closed. Reconnecting...");
        setTimeout(connectWebSocket, 1000); // Try reconnecting after a delay
    };
};

useEffect(() => {
    connectWebSocket();
}, [])

    useEffect(() => {
        // Initialize WebSocket connection once
        connection.current = new WebSocket('ws://localhost:8080');

       connection.current = new WebSocket('ws://localhost:8080');

        connection.current.onopen = () => {
    console.log('WebSocket connection established.');
        // Send the initial message to join the game
        const playerName = window.localStorage.getItem('playerName');
        const tableId = window.localStorage.getItem('tableId');
        connection.current?.send(JSON.stringify({ action: 'playGame', playerName, tableId }));
    };




        connection.current.onmessage = async (e) => {
            await new Promise(resolve => setTimeout(resolve, 500));
            const data = JSON.parse(e.data);
            console.log('Parsed data:', data);

            if (data.players) {
                const updatedPlayers = data.players.map((p: any) => ({
                    playerName: p.playerName,
                    playerCoin: p.playerCoin,
                    dealer: p.playerName === data.dealer,
                }));

                setPlayerObj(updatedPlayers);
                const storedPlayerName = window.localStorage.getItem('playerName');
                const startIndex = updatedPlayers.findIndex((p: any) => p.playerName === storedPlayerName);
                const reorderedPlayers = [
                    ...updatedPlayers.slice(startIndex),
                    ...updatedPlayers.slice(0, startIndex),
                ];

                setPlayers(reorderedPlayers.map(p => p.playerName));
                setCoins(reorderedPlayers.map(p => p.playerCoin));
                setDealer(data.dealer);
            }

            if (data.card) {
                setMyCards(data.card[0]);
                setMySecodnCard(data.card[1]);
            }

            if (data.pot !== undefined) setPot(data.pot);

            if (!(data.acceptSmallBlind === undefined)) {
                console.log(data.acceptSmallBlind);
                
            }
        };

        connection.current.onerror = (error) => {
            console.error('WebSocket Error:', error);
        };

        connection.current.onclose = () => {
            console.log('WebSocket connection closed.');
            window.localStorage.removeItem('tableId');
        };

        // Cleanup WebSocket on component unmount
        return () => {
            if (connection.current) {
                connection.current.close();
            }
        };
    }, []);

    useEffect(() => {
        if (pot) {
          console.log('pot update', pot);
            
        }
    },[pot])




    return (
        <div className="playboard">
            <div className='board-border'>
                <div className='board'>
                    {/* Render reordered players */}
                    <div className='player player-1'>
                        {images && images[2] && (<img src={require(images[2])} />)}
                        {myCards && myCards.card_link && (
                            <div className='card-imagine-div'>
                                <div className='card-imagine'></div>
                                <div className='card-imagine'></div>
                            </div>
                        )}
                        <h2 className='player-name'>{players[2] || ''}</h2>
                        <h2>{coins[2]}</h2>
                    </div>
                    <div className='player player-2'>
                        <div className='cards-div'>
                            {myCards && myCards.card_link && (
                                <img className='card first-card' src={require(`../cards/${myCards.card_link}`)} alt="" />
                            )}
                            {mySecondCard && mySecondCard.card_link && (
                                <img className='card first-card' src={require(`../cards/${mySecondCard.card_link}`)} alt="" />
                            )}
                        </div>
                        <h2 className='player-name'>{players[0] || ''}</h2>
                        <h2>{coins[0]}</h2>
                    </div>
                    <div className='player player-3'>
                        {myCards && myCards.card_link && (
                            <div className='card-imagine-div'>
                                <div className='card-imagine'></div>
                                <div className='card-imagine'></div>
                            </div>
                        )}
                        <h2 className='player-name'>{players[1] || ''}</h2>
                        <h2>{coins[1]}</h2>
                    </div>
                    <div className='pot'>
                        <h1> pot: {pot}</h1>
                        <h3>{dealer} is a dealer</h3>
                    </div>
                    <div className='player player-4'>
                        {myCards && myCards.card_link && (
                            <div className='card-imagine-div'>
                                <div className='card-imagine'></div>
                                <div className='card-imagine'></div>
                            </div>
                        )}
                        <h2 className='player-name'>{players[3] || ''}</h2>
                        <h2>{coins[3]}</h2>
                    </div>
                </div>
            </div>
            <div className='panel'>
                <button className='panel-btn fold'>Fold</button>
                <button className='panel-btn call'>Call</button>
                <button className='panel-btn raise'>Raise</button>
            </div>
        </div>
    );
};

export default Playboard;
