import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useWebSocket } from '../WebSocketContext';

const PokerComponent = () => {
    const playerName = window.localStorage.getItem('playerName');
    const [test, setTest] = useState<string | null>(null);
    const navigate = useNavigate();
    const { ws, initializeWebSocket } = useWebSocket();

    useEffect(() => {
        initializeWebSocket(); // Open WebSocket only after component renders

        if (ws) {
            ws.onopen = () => {
                ws.send(JSON.stringify({action :'joinGame'}))
            }
            ws.onmessage = (e: MessageEvent) => {
                const data = JSON.parse(e.data);
                setTest(data.message);
                if (data.tableId) {
                    navigate(`/play/${data.tableId}`);
                    window.localStorage.setItem('tableId', data.tableId);
                }

                console.log(data);
                
            };
        }
    }, []);

    return (
        <div className='gaga'>
            <h1>{test}</h1>
        </div>
    );
};

export default PokerComponent;
